USE `filmratingdb`;
INSERT INTO `filmratingdb`.`film` (`film_id`, `film_title`, `film_country`, `film_description`, `film_age_restriction`, `film_date_of_release`, `film_poster_path`) VALUES (1, 'Thor 3 : Ragnarok', 2, 'Imprisoned, the almighty Thor finds himself in a lethal gladiatorial contest against the Hulk, his former ally. Thor must fight for survival and race against time to prevent the all-powerful Hela from destroying his home and the Asgardian civilization.', 16, '2017-11-02', 'img/thor3.jpg');
INSERT INTO `filmratingdb`.`film` (`film_id`, `film_title`, `film_country`, `film_description`, `film_age_restriction`, `film_date_of_release`, `film_poster_path`) VALUES (2, 'Spider-Man: Homecoming', 2, 'Peter Parker balances his life as an ordinary high school student in Queens with his superhero alter-ego Spider-Man, and finds himself on the trail of a new menace prowling the skies of New York City.', 16, '2017-07-06', 'img/spider-man.jpg');
INSERT INTO `filmratingdb`.`film` (`film_id`, `film_title`, `film_country`, `film_description`, `film_age_restriction`, `film_date_of_release`, `film_poster_path`) VALUES (3, 'The Shawshank Redemption', 2, 'Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.', 16, '1994-10-14', 'img/shawshank.jpg');
INSERT INTO `filmratingdb`.`film` (`film_id`, `film_title`, `film_country`, `film_description`, `film_age_restriction`, `film_date_of_release`, `film_poster_path`) VALUES (4, 'Seven', 2, 'Two detectives, a rookie and a veteran, hunt a serial killer who uses the seven deadly sins as his motives.', 18, '1995-09-22', 'img/seven.jpg');
INSERT INTO `filmratingdb`.`film` (`film_id`, `film_title`, `film_country`, `film_description`, `film_age_restriction`, `film_date_of_release`, `film_poster_path`) VALUES (5, 'The Avengers', 2, 'Earth\'s mightiest heroes must come together and learn to fight as a team if they are going to stop the mischievous Loki and his alien army from enslaving humanity.', 12, '2012-05-03', 'img/avengers.jpg');
INSERT INTO `filmratingdb`.`film` (`film_id`, `film_title`, `film_country`, `film_description`, `film_age_restriction`, `film_date_of_release`, `film_poster_path`) VALUES (6, 'The Expendables', 2, 'A CIA operative hires a team of mercenaries to eliminate a Latin dictator and a renegade CIA agent.', 18, '2010-08-11', 'img/expendables.jpg');
INSERT INTO `filmratingdb`.`film` (`film_id`, `film_title`, `film_country`, `film_description`, `film_age_restriction`, `film_date_of_release`, `film_poster_path`) VALUES (7, 'Avengers: Infinity War', 2, 'The Avengers and their allies must be willing to sacrifice all in an attempt to defeat the powerful Thanos before his blitz of devastation and ruin puts an end to the universe.', 12, '2018-05-03', 'img/avenger_infinity_war.jpg');

COMMIT;


